mapper.select:
SELECT M_NO, M_ID, M_PW, M_NAME, M_EMAIL, M_ADDRESS, M_JOIN, M_GRADE, M_ADDRESS2, M_PAYMENT
FROM MMH_MEMBER
WHERE M_ID = #{m_id}
AND M_PW = #{m_pw}

SELECT M_NO, M_ID, M_PW, M_NAME, M_EMAIL, M_ADDRESS, M_JOIN, M_GRADE, M_ADDRESS2, M_PAYMENT
FROM MMH_MEMBER
WHERE M_ID = #{m_id}

mapper.update:
UPDATE MMH_MEMBER
SET M_NAME = #{m_name}, M_ADDRESS = #{m_address}, M_JOIN = #{m_join }
WHERE M_ID = #{m_id}
